import React, {useState, useEffect} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import {Modal, ModalBody, ModalFooter, ModalHeader} from 'reactstrap';


function App() {

const baseUrl="https://localhost:7062/api/students"

const [data, setData] = useState([]);
const requestGet = async()=>{
    await axios.get(baseUrl)
    .then(response => 
      {
        setData(response.data);
      }).catch(error=>{
        console.log(error);
      })
}
useEffect(()=>{
  requestGet();
})
  return (
    <div className="App">
      <br/>
      <h3>Students Register</h3>
      <header>
        <button className='btn btn-success'>Add new student</button>
      </header>
      <br/>
      <table className='table table-bordered'>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Email</th>
            <th>Age</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map(student=>(
            <tr key={student.id}>
              <td>{student.id}</td>
              <td>{student.name}</td>
              <td>{student.email}</td>
              <td>{student.age}</td>
              <td>
                <buton className="btn btn-primary">Edit</buton>{" "}
                <buton className="btn btn-danger">Delete</buton>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal>
      <ModalHeader>Add Students</ModalHeader>
      <ModalBody>
        <div className='form-group'>
          <label>Name:</label>
          <br/>
          <input type='text' className='form-control'/>
          <br/>
          <label>Email:</label>
          <br/>
          <input type='text' className='form-control'/>
          <br/>
          <label>Age:</label>
          <br/>
          <input type='text' className='form-control'/>
          <br/>
        </div>
      </ModalBody>
      </Modal>
    </div>
  );
}

export default App;
